body.messageBody:
{
    "type": "AdaptiveCard",
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.4",
    "msteams": {
        "width": "Full"
        },
    "body": [
        {
            "type": "TextBlock",
            "text": "@{triggerBody()?['object']?['properties']?['title']}",
            "wrap": true,
            "fontType": "Default",
            "size": "ExtraLarge",
            "color": "Attention",
            "isSubtle": true
        },
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "width": "stretch",
                    "items": [
                        {
                            "type": "Image",
                            "url": "https://seeklogo.com/images/M/microsoft-sentinel-azure-logo-28FA1B44A7-seeklogo.com.png"
                        }
                    ]
                },
                {
                    "type": "Column",
                    "width": "stretch",
                    "items": [
                        {
                            "type": "TextBlock",
                            "text": "**ID** : @{triggerBody()?['object']?['properties']?['incidentNumber']}",
                            "wrap": true,
                            "size": "Default",
                            "weight": "Lighter",
                            "color": "Accent"
                        },
                        {
                            "type": "TextBlock",
                            "text": "**Sevirity** : @{triggerBody()?['object']?['properties']?['severity']}",
                            "wrap": true,
                            "size": "Default",
                            "weight": "Lighter",
                            "color": "Accent"
                        },
                        {
                            "type": "TextBlock",
                            "text": "**Status** : @{triggerBody()?['object']?['properties']?['status']}",
                            "wrap": true,
                            "size": "Default",
                            "weight": "Bolder",
                            "color": "Default"
                        }, 
                        {
                            "type": "TextBlock",
                            "text": "**更新元** : @{triggerBody()?['incidentUpdates']?['updatedBy']?['source']}",
                            "wrap": true,
                            "size": "Default",
                            "weight": "Lighter",
                            "color": "Accent"
                        },
                        {
                            "type": "TextBlock",
                            "text": "**更新者**(Sentinel経由のみ) : @{triggerBody()?['incidentUpdates']?['updatedBy']?['name']}",
                            "wrap": true,
                            "size": "Default",
                            "weight": "Lighter",
                            "color": "Accent"
                        },
                        {
                            "type": "TextBlock",
                            "text": "**アサイン先** : @{triggerBody()?['object']?['properties']?['owner']?['assignedTo']}",
                            "wrap": true,
                            "size": "Default",
                            "weight": "Lighter",
                            "color": "Accent"
                        },
                        {
                            "type": "TextBlock",
                            "text": "**インシデント分類** : @{triggerBody()?['object']?['properties']?['classification']}",
                            "wrap": true,
                            "size": "Default",
                            "weight": "default",
                            "color": "default"
                        },
                        {
                            "type": "TextBlock",
                            "text": "**インシデント分類の理由** : @{triggerBody()?['object']?['properties']?['classificationReason']}",
                            "wrap": true,
                            "size": "Default",
                            "weight": "default",
                            "color": "default"
                        },
                        {
                            "type": "TextBlock",
                            "text": "**コメント**(Sentinel経由のみ) : @{triggerBody()?['object']?['properties']?['classificationComment']}",
                            "wrap": true,
                            "size": "Default",
                            "weight": "default",
                            "color": "default"
                        },
                        {
                            "type": "TextBlock",
                            "text": " @{triggerBody()?['object']?['properties']?['description']}",
                            "wrap": true,
                            "size": "Default",
                            "weight": "Lighter",
                            "color": "Default"
                        },
                        {
                            "type": "ActionSet",
                            "actions": [
                                {
                                    "type": "Action.OpenUrl",
                                    "title": "Open Microsoft Sentinel",
                                    "url": "@{triggerBody()?['object']?['properties']?['incidentUrl']}"
                                }
                            ]
                        }
                    ]
                }
            ]
        }
    ]
}
